export default function studentReducer(
  state = [] , action
) {
  switch(action.type) {
    case 'FETCH_STUDENTS':
    console.log("fetching students", action.payload)
      return action.payload ;
      case 'LOADING_STUDENTS':
      console.log("loading students")
      return state;
      case 'UPDATE_STUDENT_SUCCESS' :
        return action.payload
    default:
    return state;
  }
}
